import { USERS_INFO_SAVED, ACESS_REQUEST,
} from '../actions';

const INITIAL_STATE = {
  name: '',
  gravatarEmail: '',
  assertions: '',
  score: '',
  token: '',
  // isLoading: false,
};

function player(state = INITIAL_STATE, action) {
  switch (action.type) {
  case ACTION_LOGIN_SUCCESS:
    return {
      ...state,
      ...action.payload,
    };
  case SUCCESS:
    return {
      ...state, // sem o isLoading
    };

    // Sem o FetchStart
  default:
    return state;
  }
}

export default player;
