import { QUESTIONS_RECEIVED, FETCHING_QUESTIONS,
  ERROR_ACTION,
} from '../actions';

const INITIAL_STATE = {
  allQuestions: [],
  // isLoading: false,

};

function game(state = INITIAL_STATE, action) {
  switch (action.type) {
  case QUESTIONS_RECEIVED:
    return {
      ...state,
      allQuestions: [...action.payload.questions],
      // isLoading: false,
    };
  case FETCHING_QUESTIONS:
    return {
      ...state,
      // isLoading: true,

    };
  case ERROR_ACTION:
    return {
      ...state,
      error: action.payload,
    };
  default:
    return state;
  }
}

export default game;
